import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { chatWithRAG } from "../api";
import "../style/chatbox.css";

const Chatbox = () => {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const navigate = useNavigate();

  async function handleSend() {
    const userMessage = message.trim();
    if (!userMessage) return;

    setChatHistory((prev) => [...prev, { sender: 'user', text: userMessage }]);
    setMessage('');

    try {
      const botResponse = await chatWithRAG(userMessage);
      setChatHistory((prev) => [        ...prev,        { sender: 'bot', text: botResponse || 'No response from server' },      ]);
    } catch (error) {
      console.error('Error in handleSend:', error);
      setChatHistory((prev) => [        ...prev,        { sender: 'bot', text: 'Error: Unable to get a response.' },      ]);
    }
  }

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const handleLoginClick = () => {
    navigate('/login');
  };

  return (
    <div className="chatbox">
      <div className="chat-header">
        <button onClick={handleLoginClick} className="login-button">
          Login
        </button>
      </div>
      <div className="chat-messages">
        {chatHistory.map((chat, index) => (
          <div
            key={index}
            className={`message ${chat.sender === 'user' ? 'user-message' : 'bot-message'}`}
          >
            {chat.text}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          className="message-input"
        />
        <button onClick={handleSend} className="send-button">
          Send
        </button>
      </div>
    </div>
  );
};

export default Chatbox;
